INSERT INTO move_effect_changelog_prose VALUES(13,9,'Does not hit Pokémon under the effects of []{move:fly}.');
INSERT INTO move_effect_changelog_prose VALUES(14,9,'Does not interact with []{move:minimize}.');
INSERT INTO move_effect_changelog_prose VALUES(15,9,'Does not hit Pokémon under the effects of []{move:fly}.');
INSERT INTO move_effect_changelog_prose VALUES(16,9,'Determines damage using each participant''s base [Attack]{mechanic:attack} and the opponent''s base [Defense]{mechanic:defense} in place of calculated stats.');
INSERT INTO move_effect_changelog_prose VALUES(17,9,'[]{move:swift} can hit the user while in the air, and no other moves can.  Due to a bug, if the user fails to carry out the move''s second turn, e.g. due to [paralysis]{mechanic:paralysis}, it remains in the air until it switches out or uses Fly again successfully.  It can act as if it were on the ground in the meantime, but moves still miss it.');
INSERT INTO move_effect_changelog_prose VALUES(18,9,'Does not raise [Defense]{mechanic:defense} or [Special Defense]{mechanic:special-defense}.');
INSERT INTO move_effect_changelog_prose VALUES(19,9,'Does not raise the user''s [Special Defense]{mechanic:special-defense}.');
INSERT INTO move_effect_changelog_prose VALUES(20,9,'Heals replacement Pokémon by half of their max HP.');
INSERT INTO move_effect_changelog_prose VALUES(21,9,'Inflicts []{type:normal}-type damage.');
INSERT INTO move_effect_changelog_prose VALUES(22,9,'[]{move:swift} can hit the user while underground, and no other moves can.  Due to a bug, if the user fails to carry out the move''s second turn, e.g. due to [paralysis]{mechanic:paralysis}, it remains underground until it switches out or uses Dig again successfully.  It can act as if it were aboveground in the meantime, but moves still miss it.');
